package au.com.imed.portal.referrer.referrerportal.common.util;

import java.io.ByteArrayOutputStream;
import java.io.FileNotFoundException;
import java.io.IOException;
import java.lang.reflect.Field;
import java.text.SimpleDateFormat;
import java.util.Map;

import org.apache.commons.lang3.StringUtils;
import org.springframework.stereotype.Component;

import com.itextpdf.html2pdf.HtmlConverter;
import com.itextpdf.kernel.pdf.EncryptionConstants;
import com.itextpdf.text.pdf.PdfDate;
import com.itextpdf.text.pdf.PdfReader;
import com.itextpdf.text.pdf.PdfStamper;
import com.itextpdf.text.pdf.PdfWriter;

import au.com.imed.portal.referrer.referrerportal.rest.electronicreferral.model.ElectronicReferralForm;

@Component
public class PdfGenerator {
	private static final String IMED_LOGO = "data:image/jpeg;base64,";
//	private static final String IMED_FOOTER_TAG = "<div style=\"font-size:10px;color:white;background:#f7921e;width:100%;text-align:center;\"><p>This referral has been reformatted for display and printing purposes</p></div>";

	public byte[] generatePdfReferral(ElectronicReferralForm electronicReferralForm, boolean isPatientCopy, boolean isReferrerCopy, boolean isReferrerLoggedIn, String pdfPassword)
			throws FileNotFoundException, IOException, IllegalArgumentException, IllegalAccessException {
		SimpleDateFormat submittedDateTimeFormat = new SimpleDateFormat("dd/MM/yyyy hh:mm a z");

		StringBuffer sb = new StringBuffer();
		sb.append("<body style=\"font-size:9px;\">");
		sb.append("<table width=\"100%\" border=\"0\"><tbody>"
				+ "<tr><td><h2>I-MED Electronic Referral"+ (!isPatientCopy && electronicReferralForm.isUrgentResult()? " - <font color=\"red\"> Urgent </font> ": "") +"</h2></td>" // Send urgent flag to contact centre and referrer not for patient
				+ "<td align=\"right\"><img style=\"width:200px;height:auto;\" src=\""+ IMED_LOGO +"\"></img></td>"
						+ "</tr></tbody></table>");

		sb.append("<hr>");

		sb.append("<b>Patient information</b><br>");


		sb.append("<table width=\"100%\" border=\"0.2\" ><tbody>");

		sb.append(addEntryToPdf(electronicReferralForm, "patientName", "Name"));
		sb.append(addEntryToPdf(electronicReferralForm, "patientGender", "Gender"));
		sb.append(addEntryToPdf(electronicReferralForm, "patientDob", "DOB"));
		sb.append(addEntryToPdf(electronicReferralForm, "patientPhone", "Phone"));
		sb.append(addEntryToPdf(electronicReferralForm, "patientEmail", "Email"));

		sb.append("<tr><td>Address</td><td>");
		sb.append(printAddress(electronicReferralForm.getPatientStreet(), electronicReferralForm.getPatientSuburb(), electronicReferralForm.getPatientState(), electronicReferralForm.getPatientPostcode()));
		sb.append("<br>");
		sb.append("</td></tr>");

		sb.append(addEntryToPdf(electronicReferralForm, "patientCompensation", "Worker compensation"));
		sb.append(addEntryToPdf(electronicReferralForm, "patientPregnant", "Patient is pregnant"));
		sb.append(addEntryToPdf(electronicReferralForm, "ivContrastAllergy", "Contrast allergy"));
		sb.append(addEntryToPdf(electronicReferralForm, "ivRenal", "Renal Disease"));
		sb.append(addEntryToPdf(electronicReferralForm, "ivDiabetes", "Diabetes Metformin treatment"));
		sb.append("</tbody></table>");


		sb.append("<br><b>Examination</b><br>");

		sb.append("<table width=\"100%\" border=\"0.2\"><tbody>");
		sb.append(addEntryToPdf(electronicReferralForm, "examDetails", "Exam detail"));
		sb.append("</tbody></table>");


		sb.append("<br><b>Clinical details</b><br>");

		sb.append("<table width=\"100%\" border=\"0.2\"><tbody>");
		sb.append(addEntryToPdf(electronicReferralForm, "clinicalDetails", "Clinical detail"));
		sb.append("</tbody></table>");


		sb.append("<br><b>Referring Practitioner</b><br>");
		sb.append("<table width=\"100%\" border=\"0.2\"><tbody>");
		sb.append(addEntryToPdf(electronicReferralForm, "doctorName", "Name"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorProviderNumber", "Provider number"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorRequesterNumber", "Requester number"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorAhpra", "AHPRA number"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorEmail", "Email"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorPhone", "Phone"));
		sb.append(addEntryToPdf(electronicReferralForm, "doctorPracticeName", "Practice name"));
		sb.append("<tr><td>Address</td><td>");
		sb.append(printAddress(electronicReferralForm.getDoctorStreet(), electronicReferralForm.getDoctorSuburb(), electronicReferralForm.getDoctorState(), electronicReferralForm.getDoctorPostcode()));
		sb.append("<br>");
		sb.append("</td></tr>");
		sb.append("</tbody></table>");


		sb.append("<br><b>Cc doctor</b><br>");
		sb.append("<table width=\"100%\" border=\"0.2\"><tbody>");
		sb.append(addEntryToPdf(electronicReferralForm, "ccDoctorName", "Name"));
		sb.append(addEntryToPdf(electronicReferralForm, "ccDoctorProviderNumber", "Provider number"));
		sb.append(addEntryToPdf(electronicReferralForm, "ccDoctorRequesterNumber", "Requester number"));
		sb.append(addEntryToPdf(electronicReferralForm, "ccDoctorEmail", "Email"));
		sb.append(addEntryToPdf(electronicReferralForm, "ccDoctorPracticeName", "Practice name"));
		sb.append("<tr><td>Address</td><td>");
		sb.append(printAddress(electronicReferralForm.getCcDoctorStreet(), electronicReferralForm.getCcDoctorSuburb(), electronicReferralForm.getCcDoctorState(), electronicReferralForm.getCcDoctorPostcode()));
		sb.append("<br>");
		sb.append("</td></tr>");
		sb.append("</tbody></table>");


		sb.append("<br><b>Signature</b><br>");
		sb.append("<table width=\"100%\" border=\"0.2\"><tbody>");
		sb.append(addEntryToPdf(electronicReferralForm, "signatureName", "Name"));
		sb.append(addEntryToPdf(electronicReferralForm, "signatureDate", "Date"));
		sb.append("</tbody></table><br><br>");

		if(!isPatientCopy && !isReferrerCopy) { // It is contact centre copy
			sb.append("<div>"+ "This E-referral was generated from IOL2 " + (isReferrerLoggedIn?"Credentialed":"Non-credentialed") +".</div>");
		}
		sb.append("<div>Electronic Referral submitted at "
				+ submittedDateTimeFormat.format(electronicReferralForm.getSubmittedTime()).toUpperCase() + "</div>");
		if(isPatientCopy) {
			sb.append("<br><br><div>Your doctor has recommended that you use I-MED Radiology. You may choose another provider but please discuss this with your doctor first.</div>");
		}
		sb.append("</body>");

//		if(isPatientCopy) {
//			HtmlConverter.convertToPdf(sb.toString(), new FileOutputStream("c:\\temp\\eref_pat.pdf"));
//		} else if(isReferrerCopy) {
//			HtmlConverter.convertToPdf(sb.toString(), new FileOutputStream("c:\\temp\\eref_ref.pdf"));
//		} else {
//			HtmlConverter.convertToPdf(sb.toString(), new FileOutputStream("c:\\temp\\eref_cc.pdf"));
//		}
		
		ByteArrayOutputStream outputstream = new ByteArrayOutputStream();
		
		HtmlConverter.convertToPdf(sb.toString(), outputstream);
		
		// password protection/////////
		ByteArrayOutputStream protectionos = new ByteArrayOutputStream();
		try {
			PdfReader reader = new PdfReader(outputstream.toByteArray());
			PdfStamper stamper = new PdfStamper(reader, protectionos);
			Map<String, String> info = reader.getInfo();
			info.put("Title", "IMEDONLINE EREFERRAL");
			info.put("Subject", "IMED EREFERRAL PDF");
			info.put("Keywords", "IMED EREFERRAL PDF");
			info.put("Author", "OBTAINED BY IMEDONLINE at " + new PdfDate().toString());
			stamper.setMoreInfo(info);
			if(pdfPassword != null) {
				stamper.setEncryption(pdfPassword.getBytes(), "IMEDPDFOWNERPASSCODE".getBytes(),
						0, PdfWriter.ENCRYPTION_AES_128 | PdfWriter.DO_NOT_ENCRYPT_METADATA | PdfWriter.ALLOW_PRINTING);
			}
			stamper.close();
			reader.close();
		} catch (Exception ex) {
			ex.printStackTrace();
		}
		///////////////////
		
		return protectionos.toByteArray();
	}

	private String addEntryToPdf(ElectronicReferralForm sourceForm, String fieldName, String labelToPrint)
			throws IllegalArgumentException, IllegalAccessException {
		Field[] fields = ElectronicReferralForm.class.getDeclaredFields();

		// print field names paired with their values
		for (Field field : fields) {
			if (field.getName().equals(fieldName)) {
				field.setAccessible(true);
				String result = "<tr><td>" + labelToPrint + "</td><td>"
						+ (field.get(sourceForm) != null ? field.get(sourceForm).toString().toUpperCase() : "") + "</td></tr>";
				return result;
			}
		}
		return "";
	}
	
	private String printAddress(String street, String suburb, String state, String postalCode) {
		String addressToPrint = "";
		addressToPrint = addressToPrint + (StringUtils.isNotEmpty(street)?street:"<empty>") + ", ";
		addressToPrint = addressToPrint + (StringUtils.isNotEmpty(suburb)?suburb:"<empty>") + ", ";
		addressToPrint = addressToPrint + (StringUtils.isNotEmpty(state)?state:"<empty>") + ", ";
		addressToPrint = addressToPrint + (StringUtils.isNotEmpty(postalCode)?postalCode:"<empty>, ");
		addressToPrint = addressToPrint.replaceAll("<empty>, ", "");
		return addressToPrint.toUpperCase();
	}
}
